function incrementClick(top1)
{
    var element = document.getElementById(top1);
    var value = parseInt(element.getAttribute("value"));
    element.setAttribute("value",++value);
    element.innerHTML = value;
}
function decrementClick(top1)
{
    var element = document.getElementById(top1);
    var value = parseInt(element.getAttribute("value"));
    if(value > 0){
        element.setAttribute("value",--value);
    element.innerHTML = value;
    }
}


